mapper.select:
select * from (
    select data.*, data.region_code dept_id, jdata.name typeName,
           (select count(1) from data_check_points_device pd where pd.points_id = data.id) device_count
    from data_check_points data
    left join data_check_points_type jdata on jdata.id = data.type_id
) t

    ${ew.sqlSegment}
    ${params.dataScope}
