mapper.select:
select * from (
    select data.*, jdata.name indicatorTypeName, jdata.type
    from quota_indicator data
    left join quota_indicator_type jdata on jdata.id = data.indicator_type_id
) t

    ${ew.sqlSegment}

select min_val, max_val from quota_indicator where indicator_type_id = #{typeId}
and max_val < (select max_val from quota_indicator where id = #{id})
order by max_val desc LIMIT 0,1
