mapper.select:
select count(1)
from T_CFG_DICT


        and ID = #{id}


        and DICT_NAME = #{dictName}


        and DICT_ORDER = #{dictOrder}


        and DICT_TYPE = #{dictType}


        and PARENT_ID = #{parentId}


        and CREATE_TIME = #{createTime}

select
ID, DICT_NAME, DICT_ORDER, DICT_TYPE, PARENT_ID, CREATE_TIME
from T_CFG_DICT


        and ID = #{id}


        and DICT_NAME = #{dictName}


        and DICT_ORDER = #{dictOrder}


        and DICT_TYPE = #{dictType}


        and PARENT_ID = #{parentId}


        and PARENT_ID in (select ID from T_CFG_DICT where PARENT_ID = 1

        and ID = #{typeId}

        )


order by DICT_ORDER ASC
