mapper.select:
select count(1)
from T_CFG_EVENT_NODE_CONTACT


        and ID = #{id}


        and EVENT_ID = #{eventId}


        and NODE_ID = #{nodeId}


        and IS_HIDDEN = #{isHidden}


        and NODE_ORDER = #{order}

select
a.ID, EVENT_ID, NODE_ID, IS_HIDDEN, NODE_ORDER,b.NODE_NAME
from T_CFG_EVENT_NODE_CONTACT a
left join T_CFG_EVENT_NODE b on a.node_id=b.id


        and a.ID = #{id}


        and EVENT_ID = #{eventId}


        and NODE_ID = #{nodeId}


        and IS_HIDDEN = #{isHidden}


        and NODE_ORDER = #{order}


order by NODE_ORDER
