mapper.select:
select count(1)
from t_cfg_plan_link


        and id = #{id}


        and resource_type = #{resourceType}


        and link_id = #{linkId}


        and type = #{type}


        and resource_id = #{resourceId}

mapper.delete:
delete
from t_cfg_plan_link
where link_id=#{id} and `type`=#{type}

mapper.select:
select
id, resource_type, link_id, type, resource_id
from t_cfg_plan_link


        and id = #{id}


        and resource_type = #{resourceType}


        and link_id = #{linkId}


        and type = #{type}


        and resource_id = #{resourceId}
